      if (!hintTimers[questionKey]) {
        const newTimers = { ...hintTimers };
        newTimers[questionKey] = {
          hint1: Date.now() + 60000, // 1 minute
          hint2: Date.now() + 300000, // 5 minutes
          hint3: Date.now() + 600000  // 10 minutes
        };
        setHintTimers(newTimers);
      }
    }
