function App() {
  const [gameState, setGameState] = useState('start'); // start, playing, finished, admin
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [answers, setAnswers] = useState({});
  const [teamName, setTeamName] = useState('');
  const [startTime, setStartTime] = useState(null);
  const [endTime, setEndTime] = useState(null);
  const [selectedAnswer, setSelectedAnswer] = useState('');
  const [showResult, setShowResult] = useState(false);
  const [adminLogin, setAdminLogin] = useState('');
  const [adminPassword, setAdminPassword] = useState('');

  // Hints system
  const [hintsAvailable, setHintsAvailable] = useState([]);
  const [hintsUsed, setHintsUsed] = useState({});
  const [hintTimers, setHintTimers] = useState({});

  useEffect(() => {
    // Load saved progress
    const saved = localStorage.getItem('kyiv-quest-progress');
    if (saved) {
      const data = JSON.parse(saved);
      setCurrentQuestion(data.currentQuestion || 0);
      setAnswers(data.answers || {});
      setTeamName(data.teamName || '');
      setHintsUsed(data.hintsUsed || {});
      if (data.startTime) {
        setStartTime(data.startTime);
        setGameState('playing');
      }
    }
  }, []);

  useEffect(() => {
    // Save progress
    if (gameState === 'playing') {
      const data = {
        currentQuestion,
        answers,
        teamName,
        startTime,
        hintsUsed
      };
      localStorage.setItem('kyiv-quest-progress', JSON.stringify(data));
    }
  }, [currentQuestion, answers, teamName, startTime, gameState, hintsUsed]);

  useEffect(() => {
    // Initialize hint timers when question changes
    if (gameState === 'playing') {
      const questionKey = currentQuestion;
      if (!hintTimers[questionKey]) {
        const newTimers = { ...hintTimers };
        newTimers[questionKey] = {
          hint1: Date.now() + 60000, // 1 minute
          hint2: Date.now() + 300000, // 5 minutes
          hint3: Date.now() + 600000  // 10 minutes
        };
        setHintTimers(newTimers);
      }
    }
  }, [currentQuestion, gameState]);

  useEffect(() => {
    // Check hint availability every second
    const interval = setInterval(() => {
      if (gameState === 'playing') {
        const now = Date.now();
        const questionKey = currentQuestion;
        const timers = hintTimers[questionKey];

        if (timers) {
          const newHintsAvailable = [];
          if (now >= timers.hint1) newHintsAvailable.push(0);
          if (now >= timers.hint2) newHintsAvailable.push(1);
          if (now >= timers.hint3) newHintsAvailable.push(2);
          setHintsAvailable(newHintsAvailable);
        }
      }
    }, 1000);

    return () => clearInterval(interval);
  }, [gameState, currentQuestion, hintTimers]);

  const startGame = () => {
    if (!teamName.trim()) return;
    setStartTime(Date.now());
    setGameState('playing');
    setCurrentQuestion(0);
    setAnswers({});
    setHintsUsed({});
    setHintTimers({});
  };

  const handleAnswer = () => {
    if (!selectedAnswer || !selectedAnswer.trim()) {
      return;
    }

    const question = QUESTIONS[currentQuestion];
    const isCorrect = checkAnswer(selectedAnswer, question);

    const newAnswers = {
      ...answers,
      [currentQuestion]: {
        answer: selectedAnswer,
        correct: isCorrect,
        timestamp: Date.now()
      }
    };
    setAnswers(newAnswers);
    setShowResult(true);

    setTimeout(() => {
      setShowResult(false);
      setSelectedAnswer('');

      // Only move to next question if answer is correct
      if (isCorrect) {
        if (currentQuestion < QUESTIONS.length - 1) {
          setCurrentQuestion(currentQuestion + 1);
        } else {
          // Game finished
          setEndTime(Date.now());
          setGameState('finished');
          localStorage.removeItem('kyiv-quest-progress');
        }
      }
    }, 3000);
  };

  const checkAnswer = (answer, question) => {
    if (question.type === 'multiple') {
      return answer === question.correct;
    } else {
      const correct = question.correct.toLowerCase().trim();
      const userAnswer = answer.toLowerCase().trim();
      return userAnswer === correct || 
             userAnswer.includes(correct) || 
             correct.includes(userAnswer) ||
             (parseInt(userAnswer) && parseInt(userAnswer) === parseInt(correct));
    }
  };

  const useHint = (hintIndex) => {
    const questionKey = currentQuestion;
    const newHintsUsed = { ...hintsUsed };
    if (!newHintsUsed[questionKey]) {
      newHintsUsed[questionKey] = [];
    }
    if (!newHintsUsed[questionKey].includes(hintIndex)) {
      newHintsUsed[questionKey].push(hintIndex);
      setHintsUsed(newHintsUsed);
    }
  };

  const getCompletionTime = () => {
    if (!startTime || !endTime) return 0;
    return Math.floor((endTime - startTime) / 1000);
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const formatTimeLeft = (timestamp) => {
    const now = Date.now();
    const timeLeft = Math.max(0, Math.floor((timestamp - now) / 1000));
    const mins = Math.floor(timeLeft / 60);
    const secs = timeLeft % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const restartGame = () => {
    setGameState('start');
    setCurrentQuestion(0);
    setAnswers({});
    setTeamName('');
    setStartTime(null);
    setEndTime(null);
    setSelectedAnswer('');
    setShowResult(false);
    setHintsUsed({});
    setHintTimers({});
    localStorage.removeItem('kyiv-quest-progress');
  };

  const handleAdminLogin = () => {
    if (adminLogin === 'admin' && adminPassword === '12345') {
      setGameState('admin');
      setAdminLogin('');
      setAdminPassword('');
    } else {
      alert('Невірний логін або пароль!');
    }
  };

  const getCorrectAnswers = () => {
    return Object.values(answers).filter(a => a.correct).length;
  };

  // Admin Panel
  if (gameState === 'admin') {
    return (
      <div className="min-h-screen bg-gray-900 text-white p-6">
        <div className="max-w-4xl mx-auto">
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-3xl font-bold neon-text-purple">
              🔧 Адмін-панель KYIV Quest
            </h1>
            <button
              onClick={() => setGameState('start')}
              className="neon-button-blue px-4 py-2"
            >
              Вихід
            </button>
          </div>

          <div className="grid md:grid-cols-2 gap-6">
            <div className="neon-box p-6">
              <h2 className="text-xl font-bold mb-4 neon-text-blue">📊 Статистика</h2>
              <div className="space-y-2 text-sm">
                <p>Всього команд: {Object.keys(localStorage).filter(k => k.includes('kyiv-quest')).length}</p>
                <p>Активних ігор: -</p>
                <p>Завершених ігор: -</p>
              </div>
            </div>

            <div className="neon-box p-6">
              <h2 className="text-xl font-bold mb-4 neon-text-green">⚙️ Налаштування</h2>
              <div className="space-y-3">
                <button className="w-full py-2 neon-button-purple text-sm">
                  Редагувати питання
                </button>
                <button className="w-full py-2 neon-button-pink text-sm">
                  Налаштувати підказки
                </button>
                <button className="w-full py-2 neon-button-blue text-sm">
                  Експорт даних
                </button>
              </div>
            </div>
          </div>

          <div className="neon-box p-6 mt-6">
            <h2 className="text-xl font-bold mb-4 neon-text-purple">📝 Питання квесту</h2>
            <div className="space-y-4 max-h-96 overflow-y-auto">
              {QUESTIONS.map((q, index) => (
                <div key={q.id} className="bg-gray-800 p-4 rounded-lg">
                  <div className="flex justify-between items-start">
                    <div>
                      <h3 className="font-bold text-blue-400">
                        {index + 1}. {q.location}
                      </h3>
                      <p className="text-sm text-gray-300 mt-1">
                        {q.question.split('\n')[2] || q.question}
                      </p>
                      <p className="text-xs text-green-400 mt-1">
                        Правильна відповідь: {q.correct}
                      </p>
                    </div>
                    <button className="text-xs bg-blue-600 px-2 py-1 rounded">
                      Редагувати
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (gameState === 'start') {
    return (
      <div className="min-h-screen bg-gray-900 flex items-center justify-center p-4">
        <div className="max-w-md w-full">
          <div className="text-center mb-8">
            <h1 className="text-4xl font-bold mb-4 neon-text-blue">
              🟣 KYIV QUEST
            </h1>
            <p className="text-lg text-gray-300 mb-2">неоновий маршрут містом легенд</p>
            <div className="neon-box p-6 mb-6">
              <p className="text-gray-300 text-sm leading-relaxed">
                🧭 Привіт, мандрівнику!<br/>
                Ти вирушаєш у захопливу подорож серцем Києва — міста, де кожна вулиця шепоче історії, а кожен камінь зберігає таємниці.<br/>
                На тебе чекають 10 завдань — пізнавальні, несподівані, з ноткою магії та гумору.
              </p>
            </div>
          </div>

          <div className="space-y-4">
            <input
              type="text"
              placeholder="Назва команди"
              value={teamName}
              onChange={(e) => setTeamName(e.target.value)}
              className="w-full p-3 bg-gray-800 border border-gray-600 rounded-lg text-white neon-input"
              maxLength="50"
            />
            <button
              onClick={startGame}
              disabled={!teamName.trim()}
              className="w-full py-3 neon-button-blue disabled:opacity-50 disabled:cursor-not-allowed"
            >
              Почати квест →
            </button>
          </div>

          {/* Admin Login */}
          <div className="mt-8 pt-4 border-t border-gray-700">
            <details className="cursor-pointer">
              <summary className="text-xs text-gray-500 hover:text-gray-400">
                Адміністратор
              </summary>
              <div className="mt-3 space-y-2">
                <input
                  type="text"
                  placeholder="Логін"
                  value={adminLogin}
                  onChange={(e) => setAdminLogin(e.target.value)}
                  className="w-full p-2 bg-gray-800 border border-gray-600 rounded text-white text-sm"
                />
                <input
                  type="password"
                  placeholder="Пароль"
                  value={adminPassword}
                  onChange={(e) => setAdminPassword(e.target.value)}
                  className="w-full p-2 bg-gray-800 border border-gray-600 rounded text-white text-sm"
                />
                <button
                  onClick={handleAdminLogin}
                  className="w-full py-2 bg-gray-700 hover:bg-gray-600 rounded text-sm transition-colors"
                >
                  Увійти
                </button>
              </div>
            </details>
          </div>
        </div>
      </div>
    );
  }

  if (gameState === 'finished') {
    const completionTime = getCompletionTime();
    const correctAnswers = getCorrectAnswers();

    return (
      <div className="min-h-screen bg-gray-900 flex items-center justify-center p-4">
        <div className="max-w-md w-full text-center">
          <div className="neon-box p-8">
            <h1 className="text-3xl font-bold mb-6 neon-text-purple">
              🎉 Квест завершено!
            </h1>

            <div className="space-y-4 mb-8">
              <div className="neon-text-blue text-xl">
                Команда: {teamName}
              </div>
              <div className="text-gray-300">
                Час: {formatTime(completionTime)}
              </div>
              <div className="text-gray-300">
                Правильних відповідей: {correctAnswers}/{QUESTIONS.length}
              </div>
              <div className="text-2xl">
                {correctAnswers === QUESTIONS.length ? '🥇' : 
                 correctAnswers >= 8 ? '🥈' : 
                 correctAnswers >= 6 ? '🥉' : '🏆'}
              </div>
            </div>

            <div className="space-y-3">
              <button
                onClick={restartGame}
                className="w-full py-3 neon-button-purple"
              >
                Почати знову
              </button>
              <button
                onClick={() => navigator.share && navigator.share({
                  title: 'KYIV Quest Results',
                  text: `Команда "${teamName}" завершила KYIV Quest за ${formatTime(completionTime)} з результатом ${correctAnswers}/${QUESTIONS.length}! #KyivQuest`,
                })}
                className="w-full py-3 neon-button-pink"
              >
                Поділитися результатом
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // Playing state
  const question = QUESTIONS[currentQuestion];
  const progress = ((currentQuestion + 1) / QUESTIONS.length) * 100;
  const questionKey = currentQuestion;
  const usedHints = hintsUsed[questionKey] || [];

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      {/* Header */}
      <div className="fixed top-0 left-0 right-0 z-10 bg-gray-900 border-b border-gray-700">
        <div className="p-4">
          <div className="flex justify-between items-center mb-3">
            <div className="text-sm text-gray-400">
              {teamName}
            </div>
            <button
              onClick={restartGame}
              className="text-xs bg-gray-700 hover:bg-gray-600 px-3 py-1 rounded-full transition-colors"
            >
              Рестарт
            </button>
          </div>

          {/* Progress bar */}
          <div className="w-full bg-gray-700 rounded-full h-2 overflow-hidden">
            <div 
              className="h-full neon-progress transition-all duration-500"
              style={{ width: `${progress}%` }}
            ></div>
          </div>
          <div className="text-xs text-gray-400 mt-1 text-center">
            Питання {currentQuestion + 1} з {QUESTIONS.length}
          </div>
        </div>
      </div>

      {/* Main content */}
      <div className="pt-32 pb-32 px-4">
        <div className="max-w-md mx-auto">
          {showResult ? (
            <div className="neon-box p-6 text-center">
              <div className={`text-2xl mb-4 ${
                answers[currentQuestion]?.correct ? 'neon-text-green' : 'neon-text-red'
              }`}>
                {answers[currentQuestion]?.correct ? '✅ Правильно!' : '❌ Неправильно'}
              </div>
              {!answers[currentQuestion]?.correct && (
                <div className="text-gray-300 mb-4">
                  Правильна відповідь: {question.correct}
                </div>
              )}
              <div className="text-sm text-gray-400">
                {answers[currentQuestion]?.correct ? 
                  'Переходимо до наступного питання...' : 
                  'Спробуйте ще раз або скористайтеся підказкою'
                }
              </div>
            </div>
          ) : (
            <>
              <div className="neon-box p-6 mb-6">
                <h2 className="text-lg font-bold mb-4 neon-text-purple">
                  {question.location}
                </h2>
                <p className="text-gray-300 whitespace-pre-line leading-relaxed">
                  {question.question}
                </p>
              </div>

              <div className="space-y-4 mb-6">
                {question.type === 'multiple' ? (
                  question.options.map((option, index) => (
                    <button
                      key={index}
                      onClick={() => setSelectedAnswer(option.charAt(0))}
                      className={`w-full p-3 text-left rounded-lg border transition-all ${
                        selectedAnswer === option.charAt(0)
                          ? 'neon-button-selected' 
                          : 'bg-gray-800 border-gray-600 hover:border-blue-500'
                      }`}
                    >
                      {option}
                    </button>
                  ))
                ) : (
                  <input
                    type="text"
                    placeholder="Введи відповідь..."
                    value={selectedAnswer}
                    onChange={(e) => setSelectedAnswer(e.target.value)}
                    className="w-full p-3 bg-gray-800 border border-gray-600 rounded-lg neon-input"
                  />
                )}
              </div>

              {/* Continue Button - Centered */}
              <div className="text-center mb-6">
                <button
                  onClick={handleAnswer}
                  disabled={!selectedAnswer || !selectedAnswer.trim()}
                  className="neon-button-blue px-8 py-3 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  Продовжити →
                </button>
              </div>

              {/* Hints System */}
              <div className="neon-box-subtle p-4 mb-6">
                <h3 className="text-sm font-bold mb-3 text-yellow-400">💡 Підказки</h3>
                <div className="space-y-2">
                  {[0, 1, 2].map((hintIndex) => {
                    const isAvailable = hintsAvailable.includes(hintIndex);
                    const isUsed = usedHints.includes(hintIndex);
                    const timer = hintTimers[questionKey];
                    const timeLeft = timer ? 
                      (hintIndex === 0 ? timer.hint1 : 
                       hintIndex === 1 ? timer.hint2 : timer.hint3) : 0;

                    return (
                      <div key={hintIndex} className="flex items-center justify-between text-xs">
                        <span className="text-gray-400">
                          Підказка {hintIndex + 1}:
                        </span>
                        {isUsed ? (
                          <div className="text-yellow-300 text-left flex-1 ml-2">
                            {question.hints[hintIndex]}
                          </div>
                        ) : isAvailable ? (
                          <button
                            onClick={() => useHint(hintIndex)}
                            className="bg-yellow-600 hover:bg-yellow-500 px-2 py-1 rounded text-xs"
                          >
                            Показати
                          </button>
                        ) : (
                          <span className="text-gray-500">
                            {formatTimeLeft(timeLeft)}
                          </span>
                        )}
                      </div>
                    );
                  })}
                </div>
              </div>

              {question.bonus && (
                <div className="neon-box-subtle p-4 mb-6">
                  <div className="text-sm text-yellow-400 mb-2">🎁 Бонусне завдання:</div>
                  <div className="text-xs text-gray-400">{question.bonus}</div>
                </div>
              )}
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
